defmodule UnoxWeb.CardView do
  use UnoxWeb, :view
  alias Unox.Card

  @colors %{
    red: "#ff5555",
    blue: "#5555ff",
    green: "#55aa55",
    yellow: "#ffaa00",
    black: "#333333",
    border: "#333333"
  }

  @spec color(atom) :: String.t()
  def color(key), do: @colors[key]

  def render_card(%Card{value: value, color: color}, opts) do
    hex = color(color)
    {width, opts} = Keyword.pop(opts, :width, "100")
    {class, opts} = Keyword.pop(opts, :class, "")
    class = Enum.join(["card", class], " ")

    attributes =
      Enum.map(opts, fn {name, value} ->
        [name, value]
        |> Enum.map(&to_string/1)
        |> Enum.join("=")
      end)

    ~E"""
    <svg id="<%= Unox.Utils.id() %>" viewbox="0 0 245 365" width="<%= width %>" card-color="<%= to_string(color) %>" card-value="<%= to_string(value) %>" class="<%= class %>" <%= Enum.join(attributes, " ") %> onclick="">
