      <g transform="matrix(4,0,0,4,-1260,-2077.0865)" id="g6555">
          <rect width="60" height="90" rx="10" ry="10" x="0" y="332.36218" id="rect2987-1" style="fill:#ffffff;fill-opacity:1;fill-rule:evenodd;stroke:<%= color(:border) %>;stroke-width:0.5;stroke-miterlimit:4;stroke-dasharray:none"/>
